// Run once to initialize all caas_* variables
const keys = [
  "caas_CaaS-Encoding-In",
  "caas_CaaS-Encoding-Out",
  "caas_caas-url",
  "caas_simulatorCipher",
  "caas_simulatorIv",
  "caas_simulatorWrappedKey",
  "caas_x-src-mocks-manager"
];
for (const k of keys) {
  if (insomnia.environment.get(k) === undefined) {
    insomnia.environment.set(k, '');
  }
}
insomnia.test('Variables initialized', () => {
  insomnia.expect(keys.length >= 0).to.equal(true);
});
